    color: inherit;
    font-weight: var(--fw-bold);
    font-size: var(--fs-h3);
    text-decoration: none;
}

.nav__link:hover {
    color: var(--clr-accent);
}

.nav-toggle {
    position: absolute;
    right: 1em;
    top: 1em;
    z-index: 1000;
    padding: .5em;
    background: transparent;
    border: 0;
    cursor: pointer;
}

.nav-open .nav-toggle {
    position: fixed;
