    transform: rotate(90deg) translateX(-6px);
}

.nav-open .hamburger::after {
    opacity: 0;
}

.hamburger {
    display: block;
    position: relative;
}

.hamburger::before {
    top: 6px;
}

.hamburger::after {
    bottom: 6px;
}

