.hamburger::after {
    content: '';
    position: absolute;
    left: 0;
    right: 0;
}

.hamburger,
.hamburger::before,
.hamburger::after {
    width: 2em;
    height: 3px;
    border-radius: 1em;
    background-color: var(--clr-accent);
    transition: transform 250ms ease-in-out;
}


